#!/usr/bin/perl # rep3.pl: Uses the s and tr functions for substitution # print(“Last two digits of date of birth: “); $yearin = <STDIN> ; chop($yearin); # Remove \n else comparison will fail later $found = 0; while (<>) { @line = split(/:/) ;  # Split each line split(/\//, $line[4]);  # and again on the date field $year =  $_[2] ;  # 2-digit year extracted ... if ($year == $yearin)  {  # .. and compared with user input  $found = 1;  $line[1] =~ tr/a-z/A-Z/ ; # Name field changed to uppercase  $line[0] =~ s/^/9/ ;  # Adds a “9” prefix to the first field  $_ = join(“:”, @line)  ; # Assigning to $_ allows use of next    # two s functions without using =~  s/\s *:/:/g ;  # Removes whitespace before delimiter  s#/#-#g ;  # Delimiter in date is now the   print ;  # Print $_ } } print(“Year 19” . $yearin . “ not found\n”) if $found eq 0 ;